mapper.update:
update worker
name = #{worker.name, jdbcType=VARCHAR}, birthday = #{worker.birthday, jdbcType=DATE}, gender = #{worker.gender, jdbcType=INTEGER}, id_card_no = #{worker.idCardNo, jdbcType=VARCHAR}, register_date = #{worker.registerDate, jdbcType=DATE}, phone_no = #{worker.phoneNo, jdbcType=VARCHAR}, area_id = #{worker.areaId, jdbcType=VARCHAR}, password = #{worker.password, jdbcType=VARCHAR}, head_img = #{worker.headImg, jdbcType=VARCHAR},
where id = #{worker.id, jdbcType=VARCHAR}
